const popup=document.querySelector(".popup"),content=document.querySelector(".content"),eventButton=document.querySelector(".eventButton"),resetButton=document.querySelector(".resetButton"),closeButton=document.querySelector(".closeButton");let countClickButton=0;function closePopup(){popup.classList.remove("active"),content.classList.remove("active")}function resetCount(){countClickButton=0,resetButton.style.visibility="hidden",document.querySelector(".counting").innerText=countClickButton+" times"}function Popup(){countClickButton++,popup.classList.add("active"),content.classList.add("active"),document.querySelector(".counting").innerText=countClickButton+" times",5<countClickButton&&(resetButton.style.visibility="visible")}eventButton.addEventListener("click",Popup),resetButton.addEventListener("click",resetCount),closeButton.addEventListener("click",closePopup);