const popup=document.querySelector(".popup"),eventButton=document.querySelector(".eventButton"),resetButton=document.querySelector(".resetButton");let countClickButton=0;function resetCount(){countClickButton=0,resetButton.style.visibility="hidden",document.querySelector(".counting").innerText=countClickButton+" times"}function Popup(){countClickButton++,document.querySelector(".counting").innerText=countClickButton+" times",5<countClickButton&&(resetButton.style.visibility="visible")}eventButton.addEventListener("click",Popup),resetButton.addEventListener("click",resetCount);